((window, document) => {
  "use strict";

  XF.XBgalleryavatar = XF.Event.newHandler({
    eventType: "click",
    eventNameSpace: "XBgalleryavatar",
    options: {
      contentSelector: null,
    },

    init() {},

    click(e) {
      e.preventDefault(),
        a("#xb_avatar_choice").val(a(e.currentTarget).data("avatar-data-path")),
        a(e.currentTarget).hasClass("checked")
          ? (a("#xb_avatar_choice").val(""),
            a("div#xb_avatar_select").removeClass("checked"))
          : (a("div#xb_avatar_select").removeClass("checked"),
            a(e.currentTarget).addClass("checked"));
    },
  });

  XF.Event.register("click", "xb_avatar", "XF.XBgalleryavatar");
})(window, document);
